import tkinter as tk
from gui import NetworkToolApp
import base64
import tempfile


icon_data = """

"""
icon_binary = base64.b64decode(icon_data)
with tempfile.NamedTemporaryFile(delete=False, suffix=".ico") as tmp_file:
    tmp_file.write(icon_binary)
    tmp_file_path = tmp_file.name

if __name__ == "__main__":
    root = tk.Tk()
    app = NetworkToolApp(root)
    root.protocol("WM_DELETE_WINDOW", app.on_closing)
    root.iconbitmap(tmp_file_path)
    root.mainloop()
